create table IF NOT EXISTS user_details(user_no varchar(56) not null primary key, fname varchar(255) not null,lname varchar(255) not null, sex varchar(255), phone_no varchar(255), dob date,age int(56) ,email varchar(255))
create table IF NOT EXISTS product(user_no varchar(56) not null,prod_id varchar(56) primary key not null,name varchar(255) ,slot_booking_date date not null,need_it_by date not null, problem varchar(255) not null, foreign key (user_no) references user_details(user_no));
create table IF NOT EXISTS services(user_no varchar(56) not null,prod_id varchar(56) not null,ser_no varchar(56) not null primary key, qty int(255), estimated_price int(255) not null, foreign key (user_no) references user_details(user_no), foreign key (prod_id) references product(prod_id));
create table IF NOT EXISTS payment(user_no varchar(56) not null,prod_id varchar(56) not null,payment_no varchar(78) not null primary key,payment_type varchar(152) not null, total_price int(255) not null,foreign key (user_no) references user_details(user_no),foreign key (prod_id) references product(prod_id));
create table IF NOT EXISTS employee(emp_no varchar(56) not null primary key, fname varchar(255) not null,lname varchar(255) not null, sex varchar(50),ser_no varchar(89) not null,foreign key (ser_no) references services(ser_no));